    const extractRoleCategory = (technicalDomain) => {
    if (!technicalDomain) return "Other";

    const domainLower = technicalDomain.toLowerCase().trim();

    // Direct mappings for common abbreviations
    const directMappings = {
        'ba': 'Business Analyst',
        'pm': 'Project Manager',
        'po': 'Product Owner',
        'sa': 'Solution Architect',
        'qa': 'QA Engineer',
        'fe': 'Frontend Developer',
