        'pe': 'Presales Engineer',
        'ta': 'Technical Architect',
        'tl': 'Tech Lead',
        'em': 'Engineering Manager',
        'sm': 'Scrum Master'
    };

    // Check direct mappings first
    if (directMappings[domainLower]) {
        return directMappings[domainLower];
    }

    // Then check for keyword matches
    if (domainLower.includes('business analyst')) return 'Business Analyst';
    if (domainLower.includes('project manager')) return 'Project Manager';
    if (domainLower.includes('product owner')) return 'Product Owner';
    if (domainLower.includes('solution architect')) return 'Solution Architect';

    if (domainLower.includes('frontend') || 
        domainLower.includes('react') || 
        domainLower.includes('angular') || 
        domainLower.includes('vue') || 
        domainLower.includes('slice')) {
        return 'Frontend Developer';
    }

    if (domainLower.includes('backend') || 
        domainLower.includes('java') || 
        domainLower.includes('php') || 
        domainLower.includes('python') || 
        domainLower.includes('.net') || 
        domainLower.includes('node') || 
        domainLower.includes('ruby') ||
        domainLower.includes('cms')) {
        return 'Backend Developer';
    }

    if (domainLower.includes('fullstack') || 
        domainLower.includes('full stack') || 
        domainLower.includes('full-stack')) {
        return 'Full Stack Developer';
    }

    if (domainLower.includes('mobile') || 
        domainLower.includes('android') || 
        domainLower.includes('ios') || 
        domainLower.includes('flutter') || 
        domainLower.includes('react native')) {
        return 'Mobile Developer';
    }

    if (domainLower.includes('devops') || 
        domainLower.includes('sre') || 
        domainLower.includes('infrastructure') || 
        domainLower.includes('cloud')) {
        return 'DevOps Engineer';
    }

    if (domainLower.includes('test') || 
        domainLower.includes('quality')) {
        return 'QA Engineer';
    }

    if (domainLower.includes('data scientist') || 
        domainLower.includes('data science')) {
        return 'Data Scientist';
    }

    if (domainLower.includes('data') || 
        domainLower.includes('bi') || 
        domainLower.includes('business intelligence')) {
        return 'Data Engineer';
    }

    if (domainLower.includes('machine learning') || 
        domainLower.includes('ai engineer')) {
        return 'AI/ML Engineer';
    }

    if (domainLower.includes('design') || 
        domainLower.includes('ux') || 
        domainLower.includes('ui')) {
        return 'UI/UX Designer';
    }

    if (domainLower.includes('support')) {
        return 'Support Engineer';
    }

    if (domainLower.includes('presales') || 
        domainLower.includes('pre-sales')) {
        return 'Presales Engineer';
    }

    // Return the original if no mapping found
    return technicalDomain;
};

