/**
 * Improved gibberish detection with reduced false positives
 * @param {string} input - The user input to check
 * @returns {boolean} - True if input is likely gibberish, false otherwise
 */
function isGibberish(input) {
  // Normalize input
  const text = input.trim().toLowerCase();

  // Skip very short inputs and questions
  if (text.length <= 2) return false;
  if (text.includes('?') || text.includes('!')) return false;

  // If contains multiple English words, it's not gibberish
  const words = text.split(/\s+/);
  const commonWords = [
    'a', 'an', 'the', 'this', 'that', 'these', 'those', 'it', 'is', 'are', 'am', 'was', 'were',
    'be', 'been', 'being', 'have', 'has', 'had', 'do', 'does', 'did', 'will', 'would', 'shall',
    'should', 'may', 'might', 'must', 'can', 'could', 'i', 'you', 'he', 'she', 'we', 'they',
    'me', 'him', 'her', 'us', 'them', 'my', 'your', 'his', 'our', 'their', 'mine', 'yours',
    'hers', 'ours', 'theirs', 'what', 'which', 'who', 'whom', 'whose', 'when', 'where', 'why',
    'how', 'and', 'but', 'or', 'if', 'then', 'else', 'for', 'so', 'not', 'to', 'of', 'in',
    'on', 'at', 'by', 'with', 'about', 'against', 'before', 'after', 'above', 'below', 'up',
    'down', 'out', 'off', 'over', 'under', 'again', 'further', 'then', 'once', 'here', 'there',
    'all', 'any', 'both', 'each', 'few', 'more', 'most', 'other', 'some', 'such', 'only', 'own',
    'same', 'too', 'very', 'just', 'now', 'also', 'like', 'even', 'back', 'still', 'well',
    'quite', 'rather', 'best', 'ever', 'yet', 'way', 'days', 'time', 'person', 'year', 'thing',
    'want', 'need', 'use', 'make', 'look', 'try', 'tell', 'ask', 'work', 'seem', 'feel', 'try',
    'leave', 'call', 'good', 'bad', 'new', 'first', 'last', 'long', 'great', 'little', 'get',
    'go', 'come', 'know', 'take', 'see', 'say', 'think', 'find', 'give', 'help', 'certificates',
    'we', 'have', 'do', 'what'
  ];

  // Count recognized words
  let recognizedCount = 0;
  for (const word of words) {
    if (word.length > 0 && commonWords.includes(word)) {
      recognizedCount++;
    }
  }

  // If multiple known words are found, it's not gibberish
  if (words.length >= 3 && recognizedCount >= 2) return false;
  if (recognizedCount / words.length > 0.25) return false;

  // Now check for gibberish patterns

  // Check for repeated patterns like "erterert"
  let hasRepeatedPatterns = false;

  // Check for 2-3 character repeated sequences
  for (let i = 2; i <= 3; i++) {
    if (text.length < i * 2) continue;

    for (let j = 0; j <= text.length - i * 2; j++) {
      const pattern = text.substring(j, j + i);
      // Look for the pattern repeating at least twice consecutively
      const repeatedPattern = pattern.repeat(2);
      if (text.substring(j, j + i * 2) === repeatedPattern) {
        // Make sure it's a significant part of the text (avoid catching small repeats in normal text)
        if (i * 2 > text.length / 3) {
          hasRepeatedPatterns = true;
          break;
        }
      }
    }
    if (hasRepeatedPatterns) break;
  }

  // Check for keyboard patterns/rolls
  const keyboardPatterns = [
    'qwert', 'asdfg', 'zxcvb', 'yuiop', 'hjkl', 'bnm',
    'qazwsx', 'wsxedc', 'edcrfv'
  ];

  const containsKeyboardPattern = keyboardPatterns.some(pattern => 
    text.includes(pattern)
  );

  // Check for unusual character repetition
  const hasExcessiveRepetition = /(.)\1{3,}/.test(text); // 4+ of same character

  // Check for unusual consonant sequences (4+ consonants in a row)
  const hasLongConsonantGroups = /[bcdfghjklmnpqrstvwxyz]{4,}/i.test(text);

  // Combine checks - require multiple strong indicators
  // Only flag as gibberish if multiple indicators are present or a single very strong one

  let gibberishScore = 0;

  if (hasRepeatedPatterns) gibberishScore += 3;
  if (containsKeyboardPattern) gibberishScore += 3;
  if (hasExcessiveRepetition) gibberishScore += 2;
  if (hasLongConsonantGroups) gibberishScore += 2;

  // Short strings with no recognizable words
  if (text.length < 10 && recognizedCount === 0) gibberishScore += 2;

  // Final decision - higher threshold to reduce false positives
  return gibberishScore >= 3;
}

