const aiResponse = items[0].json.response.text;

// Fix bold formatting - convert **text** to *text*
let fixedResponse = aiResponse.replace(/\*\*(.*?)\*\*/g, '*$1*');

// Fix italic formatting - convert both "word" and _word_ to _word_
fixedResponse = fixedResponse.replace(/"([^"\n]+?)"/g, '_$1_');
fixedResponse = fixedResponse.replace(/"([^'\n]+?)'/g, '_$1_');
fixedResponse = fixedResponse.replace(/\*([^*\n]+?)\*/g, '*$1*'); // Fix any regular markdown italics

// Fix links - convert different link formats to Slack's <url|text>
// Handle markdown style links [text](url)
fixedResponse = fixedResponse.replace(/\[(.*?)\]\((.*?)\)/g, '<$2|$1>');

// Handle plain URLs that should be linkified
// This regex looks for URLs not already in Slack format
const urlRegex = /(?<!<)(https?:\/\/[^\s<]+)(?![\w\s]*[|>])/g;
fixedResponse = fixedResponse.replace(urlRegex, '<$1>');

// Fix mailto links specifically
fixedResponse = fixedResponse.replace(/\[([^[\]]+)\]\(mailto:([^()]+)\)/g, '<mailto:$2|$1>');
fixedResponse = fixedResponse.replace(/(?<!<)mailto:([^\s<]+)(?![\w\s]*[|>])/g, '<mailto:$1>');

// Ensure proper spacing for blockquotes (should be > at beginning of line)
fixedResponse = fixedResponse.replace(/^>\s*/gm, '> ');

// Handle code blocks - convert triple backticks to single backticks for inline code
// First save multi-line code blocks to replace them later
const codeBlocks = [];
fixedResponse = fixedResponse.replace(/```(?:\w+)?\n([\s\S]*?)\n```/g, (match, code) => {
  const index = codeBlocks.length;
  codeBlocks.push(code);
  return `__CODE_BLOCK_${index}__`;
});

// For each code block, format it properly for Slack
// Slack uses ``` for multi-line code blocks similar to markdown
for (let i = 0; i < codeBlocks.length; i++) {
  // For multi-line code blocks, maintain the ``` format but ensure proper spacing
  fixedResponse = fixedResponse.replace(`__CODE_BLOCK_${i}__`, 
    '```\n' + codeBlocks[i].trim() + '\n```');
}

// List indentation handling
// Slack lists don't handle multiple levels of indentation well, so we'll normalize them
let lines = fixedResponse.split('\n');
let inList = false;
let listIndentLevel = 0;

for (let i = 0; i < lines.length; i++) {
  // Check for list items
  const listMatch = lines[i].match(/^(\s*)([*+-]|\d+\.)\s/);

  if (listMatch) {
    // This is a list item
    inList = true;
    const indent = listMatch[1].length;

    // Determine indentation level (0 for top level, 1+ for nested)
    const currentLevel = Math.floor(indent / 2);

    if (currentLevel > 0) {
      // For indented list items in Slack, we'll use spaces for indentation
      // Slack typically uses 2 spaces per level
      const slackIndent = '  '.repeat(currentLevel);
      lines[i] = slackIndent + lines[i].trim();
    } else {
      // Top-level list items should be at the margin
      lines[i] = lines[i].trim();
    }

    listIndentLevel = currentLevel;
  } else if (inList && lines[i].trim().length > 0 && lines[i].match(/^\s+/)) {
    // This is likely a continuation of a list item
    // Preserve the current indentation level plus one more
    const slackIndent = '  '.repeat(listIndentLevel + 1);
    lines[i] = slackIndent + lines[i].trim();
  } else {
    inList = false;
    listIndentLevel = 0;
  }
}

fixedResponse = lines.join('\n');

// Check if any markdown links were missed
if (fixedResponse.includes('[') && fixedResponse.includes('](')) {
  // Add a warning to the output
  console.log("Warning: Some markdown links may not have been properly converted");
}

// Return both the original and fixed responses
return {
  json: {
    originalResponse: aiResponse,
    fixedResponse: fixedResponse,
    formattingFixed: aiResponse !== fixedResponse
  }
};
