// Process resources
const item = items[0];
const resources = item.json.items || [];

return resources.map(resource => {
  // skills senior
  const skills_senior = [];
  const ssFields = resource.dataFields?.multiSelect?.find(t => t.id === '124830');
  if (ssFields?.values) {
      ssFields.values.forEach(v => {
          if (v.readableValue && !skills_senior.includes(v.readableValue)) {
              skills_senior.push(v.readableValue);
          }
      });
  }
  // skills midium
  const skills_medium = [];
  const smFields = resource.dataFields?.multiSelect?.find(t => t.id === '124831');
  if (smFields?.values) {
      smFields.values.forEach(v => {
          if (v.readableValue && !skills_medium.includes(v.readableValue)) {
              skills_medium.push(v.readableValue);
          }
      });
  }

  // skills junior
  const skills_junior = [];
  const sjFields = resource.dataFields?.multiSelect?.find(t => t.id === '124832');
  if (sjFields?.values) {
      sjFields.values.forEach(v => {
          if (v.readableValue && !skills_junior.includes(v.readableValue)) {
              skills_junior.push(v.readableValue);
          }
      });
  }

  // get certs
  const certs = [];
  const certFields = resource.dataFields?.multiSelect?.find(t => t.id === '124853');
  if (certFields?.values) {
      certFields.values.forEach(v => {
          if (v.readableValue && !certs.includes(v.readableValue)) {
              certs.push(v.readableValue);
          }
      });
  }

  // Extract industries
  const industriesarr = [];
  const industryField = resource.dataFields?.multiSelect?.find(ms => 
      ms.readableValue === 'Industry Expertise'
  );
  if (industryField?.values) {
    industryField.values.forEach(v => {
      if (v.readableValue) {
        industriesarr.push(v.readableValue);
      }
    });
  }

  // Get description
  const descField = resource.dataFields?.texts?.find(t => t.id === '121631');
  let description = descField?.text ?? '';

  // Get notes
  const noteField = resource.dataFields?.texts?.find(t => t.id === '123740');
  let note = noteField?.text ?? '';


  // Get Section
  const secField = resource.dataFields.listValues?.find(t => t.id === '114287');
  let section = secField?.readableValue ?? '';

  // Get seniority
  const senField = resource.dataFields?.listValues?.find(t => t.id === '115797')
  let seniority = senField?.readableValue ?? '';

  // Get team
  const teamField = resource.dataFields?.listValues?.find(t => t.id === '120128')
  let team = teamField?.readableValue ?? '';

  // Get department
  const departmentField = resource.dataFields?.listValues?.find(t => t.id === '124673')
  let department = departmentField?.readableValue ?? '';

  // Get vertical
  const verticalField = resource.dataFields?.listValues?.find(t => t.id === '124674')
  let vertical = verticalField?.readableValue ?? '';

  // Get coe_manager
  const coe_managerField = resource.dataFields?.listValues?.find(t => t.id === '124407')
  let coe_manager = coe_managerField?.readableValue ?? '';

  // Get el
  const elField = resource.dataFields?.listValues?.find(t => t.id === '124300')
  let el = elField?.readableValue ?? '';

  // Get eh
  const ehField = resource.dataFields?.listValues?.find(t => t.id === '124301')
  let eh = ehField?.readableValue ?? '';

  // Get director
  const directorField = resource.dataFields?.listValues?.find(t => t.id === '124302')
  let director = directorField?.readableValue ?? '';

  // Get employment_type
  const employment_typeField = resource.dataFields?.listValues?.find(t => t.id === '124741')
  let employment_type = employment_typeField?.readableValue ?? '';


  /**
   * Zlatko import for post processing and embed prep
   */

  // Extract all available fields from the input
  const resourceId = resource.id;
  const resourceName = resource.name;
  const technicalDomain = section;
  const globalSeniority = seniority;
  const coeManager = coe_manager;
  const engineeringLead = el;
  const engineeringHead = eh;
  const employmentType = employment_type;
  const skillSeniorLevel = skills_senior.join(", ") || "";
  const skillMidLevel = skills_medium.join(", ") || ""
  const skillJuniorLevel = skills_junior.join(", ") || "";
  const industryExpertise = industriesarr.join(", ") || "";
  const domainKnowledge = description;
  const certificates = certs.join(", ") || "";


      // Universal parser function that handles parentheses, brackets, and quoted text
    const smartParse = (textString) => {
        if (!textString) return [];

        const results = [];
        let currentItem = '';
        let insideParentheses = 0;  // Track nested parentheses
        let insideBrackets = 0;     // Track nested brackets
        let insideQuotes = false;   // Track quoted text

        // Process character by character
        for (let i = 0; i < textString.length; i++) {
            const char = textString[i];

            // Handle different states
            if (char === '(') {
                insideParentheses++;
                currentItem += char;
            } else if (char === ')') {
                insideParentheses = Math.max(0, insideParentheses - 1); // Guard against mismatched parentheses
                currentItem += char;
            } else if (char === '[') {
                insideBrackets++;
                currentItem += char;
            } else if (char === ']') {
                insideBrackets = Math.max(0, insideBrackets - 1); // Guard against mismatched brackets
                currentItem += char;
            } else if (char === '"' || char === "'") {
                insideQuotes = !insideQuotes;
                currentItem += char;
            } else if (char === ',' && !insideParentheses && !insideBrackets && !insideQuotes) {
                // Only split on commas when not inside special characters
                results.push(currentItem.trim());
                currentItem = '';
            } else {
                currentItem += char;
            }
        }

        // Add the last item if there is one
        if (currentItem.trim()) {
            results.push(currentItem.trim());
        }

        // Post-processing: Handle special formats and clean up
        return results
            .map(item => {
                // Clean up quote marks if they balance at beginning/end
                if ((item.startsWith('"') && item.endsWith('"')) || 
                    (item.startsWith("'") && item.endsWith("'"))) {
                    item = item.slice(1, -1).trim();
                }

                // Remove any trailing dots or punctuation
                return item.replace(/[.,;]+$/, '').trim();
            })
            .filter(Boolean);
    };

    // Clean and standardize a skill/domain string
    const cleanField = (text) => {
        if (!text) return text;

        // Remove redundant whitespace
        text = text.replace(/\s+/g, ' ').trim();

        // Standardize common variations (could be expanded based on your data)
        const standardizations = {
            'Javascript': 'JavaScript',
            'React.js': 'React',
            'React.JS': 'React',
            'ReactJS': 'React',
            'Node.js': 'Node.js',
            'NodeJS': 'Node.js',
            'Artificial Intelligence': 'AI',
            'Machine Learning': 'ML',
            '.NET Core': '.NET Core',
            '.Net Core': '.NET Core',
            'Devops': 'DevOps',
            'CI/CD': 'CI/CD',
            'CI / CD': 'CI/CD',
            'Front-end': 'Frontend',
            'Front end': 'Frontend',
            'Back-end': 'Backend',
            'Back end': 'Backend',
            'Next.js': 'Next.js',
            'NextJS': 'Next.js',
            'Vue.js': 'Vue.js',
            'VueJS': 'Vue.js',
            'Express.js': 'Express',
            'ExpressJS': 'Express'
        };

        // Apply standardizations
        Object.keys(standardizations).forEach(key => {
            // Use word boundaries for more precise replacement
            const regex = new RegExp(`\\b${key}\\b`, 'gi');
            text = text.replace(regex, standardizations[key]);
        });

        return text;
    };

    // Extract role category from technical domain
    const extractRoleCategory = (technicalDomain) => {
        if (!technicalDomain) return "Other";

        const domainLower = technicalDomain.toLowerCase();

        // Map technical domains to general role categories
        if (domainLower === 'ba' || domainLower.includes('business analyst')) {
            return 'Business Analyst';
        }
        if (domainLower === 'pm' || domainLower.includes('project manager')) {
            return 'Project Manager';
        }
        if (domainLower === 'po' || domainLower.includes('product owner')) {
            return 'Product Owner';
        }
        if (domainLower === 'sa' || domainLower.includes('solution architect')) {
            return 'Solution Architect';
        }
        if (domainLower.includes('frontend') || domainLower === 'fe' || 
            domainLower.includes('react') || domainLower.includes('angular') || 
            domainLower.includes('vue') || domainLower.includes('slice')) {
            return 'Frontend Developer';
        }
        if (domainLower.includes('backend') || domainLower === 'be' || 
            domainLower.includes('java') || domainLower.includes('php') || 
            domainLower.includes('python') || domainLower.includes('.net') || 
            domainLower.includes('node') || domainLower.includes('ruby') ||
            domainLower.includes('cms')) {
            return 'Backend Developer';
        }
        if (domainLower.includes('fullstack') || domainLower === 'fs' || 
            domainLower.includes('full stack') || domainLower.includes('full-stack')) {
            return 'Full Stack Developer';
        }
        if (domainLower.includes('mobile') || domainLower.includes('android') || 
            domainLower.includes('ios') || domainLower.includes('flutter') || 
            domainLower.includes('react native')) {
            return 'Mobile Developer';
        }
        if (domainLower.includes('devops') || domainLower.includes('sre') || 
            domainLower.includes('infrastructure') || domainLower.includes('cloud')) {
            return 'DevOps Engineer';
        }
        if (domainLower === 'qa' || domainLower.includes('test') || 
            domainLower.includes('quality')) {
            return 'QA Engineer';
        }
        if (domainLower.includes('data') || domainLower.includes('bi') || 
            domainLower.includes('business intelligence')) {
            return 'Data Engineer';
        }
        if (domainLower === 'data science' || domainLower.includes('ml') || domainLower.includes('ai') || 
            domainLower.includes('machine learning') || domainLower.includes('data science')) {
            return 'AI/ML Engineer';
        }
        if (domainLower.includes('design') || domainLower.includes('ux') || domainLower.includes('ui')) {
            return 'UI/UX Designer';
        }
        if (domainLower.includes('support')) {
            return 'Support Engineer';
        }

        // Return the original technical domain if no mapping found
        return technicalDomain;
    };

    // Extract additional technical skills from domain knowledge
    const extractTechnicalDetails = (domainKnowledge) => {
        if (!domainKnowledge) return { tools: [], technologies: [], methodologies: [] };

        // Common tools, technologies and methodologies to look for
        const tools = [
            'Jira', 'Confluence', 'Azure DevOps', 'GitHub', 'GitLab', 'Bitbucket',
            'Jenkins', 'TeamCity', 'Travis CI', 'CircleCI', 'Bamboo',
            'Slack', 'Microsoft Teams', 'Asana', 'Trello', 'Monday',
            'Figma', 'Sketch', 'Adobe XD', 'InVision', 'Zeplin',
            'Power BI', 'Tableau', 'Looker', 'Grafana', 'Kibana',
            'Postman', 'Swagger', 'SonarQube', 'Selenium', 'BPMN', 'UML',
            'Axure', 'Adobe Photoshop', 'Adobe Illustrator', 'InDesign'
        ];

        const technologies = [
            'React', 'Angular', 'Vue', 'Next.js', 'Nuxt', 'Svelte',
            'Node.js', 'Express', 'Django', 'Flask', 'Spring Boot', 'Laravel', 'ASP.NET',
            'MongoDB', 'PostgreSQL', 'MySQL', 'SQL Server', 'Oracle', 'DynamoDB', 'Cassandra',
            'Docker', 'Kubernetes', 'AWS', 'Azure', 'GCP', 'Terraform', 'Ansible', 'Puppet',
            'TensorFlow', 'PyTorch', 'Keras', 'scikit-learn', 'pandas', 'NumPy',
            'Hadoop', 'Spark', 'Kafka', 'Airflow', 'Databricks',
            'REST', 'GraphQL', 'SOAP', 'API', 'Microservices', 'ERP', 'CRM',
            'Firebase', 'Cognito', 'Auth0', 'Mobile banking', 'Payment gateway'
        ];

        const methodologies = [
            'Agile', 'Scrum', 'Kanban', 'SAFe', 'Waterfall', 'Lean', 'DevOps',
            'CI/CD', 'TDD', 'BDD', 'DDD', 'Microservices', 'Serverless',
            'User research', 'Design thinking', 'User-centered design',
            'Data modeling', 'ETL', 'Data warehousing', 'OLAP',
            'Product discovery', 'User story mapping', 'Requirements elicitation',
            'Business analysis', 'Product ownership', 'Continuous integration'
        ];

        // Extract matches
        const findMatches = (text, terms) => {
            const matches = [];
            const lowerText = text.toLowerCase();

            terms.forEach(term => {
                if (lowerText.includes(term.toLowerCase())) {
                    matches.push(term);
                }
            });

            return [...new Set(matches)]; // Remove duplicates
        };

        return {
            tools: findMatches(domainKnowledge, tools),
            technologies: findMatches(domainKnowledge, technologies),
            methodologies: findMatches(domainKnowledge, methodologies)
        };
    };

    // Determine seniority level based on Global Seniority 
    const determineSeniorityLevel = (globalSeniority) => {
        // If global seniority is not available, return unknown
        if (!globalSeniority) return "Unknown";

        const seniorityLower = globalSeniority.toLowerCase();

        if (seniorityLower.includes('senior')) {
            // Extract number if available (e.g., "Senior 2" -> 2)
            const seniorityNum = seniorityLower.match(/\d+/);
            if (seniorityNum) {
                return `Senior ${seniorityNum[0]}`;
            }
            return "Senior";
        }

        if (seniorityLower.includes('mid')) {
            // Extract number if available (e.g., "Mid 2" -> 2)
            const midNum = seniorityLower.match(/\d+/);
            if (midNum) {
                return `Mid ${midNum[0]}`;
            }
            return "Mid";
        }

        if (seniorityLower.includes('junior')) {
            // Extract number if available (e.g., "Junior 2" -> 2)
            const juniorNum = seniorityLower.match(/\d+/);
            if (juniorNum) {
                return `Junior ${juniorNum[0]}`;
            }
            return "Junior";
        }

        if (seniorityLower.includes('head')) {
            return "Head";
        }

        if (seniorityLower.includes('manager')) {
            return "Manager";
        }

        // Return original if no mapping
        return globalSeniority;
    };

    // Determine if the person is a manager or lead
    const isLeadOrManager = (globalSeniority, engineeringLead, coeManager) => {
        if (!globalSeniority && !engineeringLead && !coeManager) return false;

        // Check if global seniority indicates management position
        if (globalSeniority && (
            globalSeniority.toLowerCase().includes('manager') ||
            globalSeniority.toLowerCase().includes('head')
        )) {
            return true;
        }

        // Check if they are listed as engineering lead for anyone
        if (engineeringLead && engineeringLead === resourceName) {
            return true;
        }

        // Check if they are listed as COE manager for anyone
        if (coeManager && coeManager === resourceName) {
            return true;
        }

        return false;
    };

    // Create skills arrays
    const skills = {
        senior: smartParse(skillSeniorLevel).map(cleanField),
        mid: smartParse(skillMidLevel).map(cleanField),
        junior: smartParse(skillJuniorLevel).map(cleanField)
    };

    // Extract all skills for quick access and filtering
    const allSkills = [
        ...skills.senior.map(skill => ({ skill, level: 'senior' })),
        ...skills.mid.map(skill => ({ skill, level: 'mid' })),
        ...skills.junior.map(skill => ({ skill, level: 'junior' }))
    ];

    // Get flat list of skills
    const flatSkills = allSkills.map(s => s.skill);

    // Extract technical details from domain knowledge
    const technicalDetails = extractTechnicalDetails(domainKnowledge);

    // Add extracted technical details to all_skills if not already present
    technicalDetails.tools.forEach(tool => {
        if (!flatSkills.includes(tool)) {
            flatSkills.push(tool);
        }
    });

    technicalDetails.technologies.forEach(tech => {
        if (!flatSkills.includes(tech)) {
            flatSkills.push(tech);
        }
    });

    technicalDetails.methodologies.forEach(method => {
        if (!flatSkills.includes(method)) {
            flatSkills.push(method);
        }
    });

    // Determine primary skill areas based on skills
    const identifyPrimarySkillAreas = (allSkills) => {
        const skillGroups = {
            'Frontend': ['React', 'Angular', 'Vue', 'JavaScript', 'TypeScript', 'HTML', 'CSS', 'SASS', 'LESS', 'Redux', 'Next.js'],
            'Backend': ['Java', 'Spring', 'Python', 'Django', 'FastAPI', 'PHP', 'Laravel', 'Symfony', 'C#', '.NET', 'Node.js', 'Express', 'Ruby', 'Rails'],
            'Database': ['SQL', 'PostgreSQL', 'MySQL', 'MongoDB', 'DynamoDB', 'Elasticsearch', 'Redis', 'Cassandra'],
            'DevOps': ['Docker', 'Kubernetes', 'AWS', 'Azure', 'GCP', 'CI/CD', 'Jenkins', 'Terraform', 'Ansible'],
            'Mobile': ['Android', 'iOS', 'Swift', 'Kotlin', 'Flutter', 'React Native'],
            'AI/ML': ['TensorFlow', 'PyTorch', 'ML', 'AI', 'NLP', 'Computer Vision', 'Data Science'],
            'Testing': ['QA', 'Selenium', 'Cypress', 'Jest', 'Testing', 'TDD', 'BDD', 'JUnit', 'PyTest'],
            'Management': ['Agile', 'Scrum', 'Kanban', 'Project Management', 'Product Management', 'JIRA', 'Business Analysis'],
            'Design': ['UI', 'UX', 'Figma', 'Sketch', 'Adobe XD', 'Photoshop', 'Illustrator']
        };

        // Count occurrences of skills in each area
        const areaScores = {};
        Object.keys(skillGroups).forEach(area => {
            areaScores[area] = 0;
            skillGroups[area].forEach(skill => {
                allSkills.forEach(userSkill => {
                    if (userSkill.toLowerCase().includes(skill.toLowerCase())) {
                        areaScores[area]++;
                    }
                });
            });
        });

        // Return areas with at least one skill match, sorted by score
        const matchedAreas = Object.keys(areaScores)
            .filter(area => areaScores[area] > 0)
            .sort((a, b) => areaScores[b] - areaScores[a]);

        return matchedAreas;
    };

    // Get role category from technical domain
    const roleCategory = extractRoleCategory(technicalDomain);

    // Get seniority level
    const seniorityLevel = determineSeniorityLevel(globalSeniority);

    // Check if the person is a lead or manager
    const isLeadOrManagerRole = isLeadOrManager(globalSeniority, engineeringLead, coeManager);

    // Get primary skill areas
    const primarySkillAreas = identifyPrimarySkillAreas(flatSkills);

    // Parse industries
    const industries = smartParse(industryExpertise).map(cleanField);

    // Parse certificates
    const certificatesList = smartParse(certificates).map(cleanField);

    // Extract organizational structure
    const orgStructure = {
        team: team,
        department: department,
        vertical: vertical,
        reports_to: {
            engineering_lead: engineeringLead !== resourceName ? engineeringLead : null,
            coe_manager: coeManager,
            engineering_head: engineeringHead,
            director: director
        },
        is_lead_or_manager: isLeadOrManagerRole
    };

    // Build natural language descriptions for better LLM understanding

    // 1. Role description
    let roleDescription = `${resourceName} is a ${roleCategory}`;
    if (globalSeniority) {
        roleDescription += ` with ${globalSeniority} seniority`;
    }
    if (isLeadOrManagerRole) {
        roleDescription += ` in a leadership position`;
    }
    if (primarySkillAreas.length > 0) {
        roleDescription += ` specializing in ${primarySkillAreas.slice(0, 2).join(' and ')}`;
    }
    roleDescription += `.`;

    // 2. Skills description
    let skillsDescription = "";

    if (skills.senior.length > 0) {
        skillsDescription += `Has senior-level expertise in ${skills.senior.join(', ')}.`;
    }

    if (skills.mid.length > 0) {
        skillsDescription += ` Proficient in ${skills.mid.join(', ')}.`;
    }

    if (skills.junior.length > 0) {
        skillsDescription += ` Also familiar with ${skills.junior.join(', ')}.`;
    }

    // 3. Industry description
    let industryDescription = "";
    if (industries.length > 0) {
        industryDescription = `Has experience in the following industries: ${industries.join(', ')}.`;
    }

    // 4. Organizational context
    let orgDescription = "";
    if (team || department || vertical) {
        orgDescription = `Works in ${team || 'the company'}`;
        if (department) {
            orgDescription += `, part of ${department}`;
        }
        if (vertical) {
            orgDescription += ` within the ${vertical} vertical`;
        }
        orgDescription += '.';
    }

    // 5. Management chain
    let managementDescription = "";
    if (coeManager || engineeringLead || engineeringHead || director) {
        const managers = [];
        if (engineeringLead && engineeringLead !== resourceName) managers.push(`Engineering Lead: ${engineeringLead}`);
        if (coeManager) managers.push(`COE Manager: ${coeManager}`);
        if (engineeringHead) managers.push(`Engineering Head: ${engineeringHead}`);
        if (director) managers.push(`Director: ${director}`);

        if (managers.length > 0) {
            managementDescription = `Reports to ${managers.join(', ')}.`;
        }
    }

    // 6. Domain knowledge, technical tools and certificates
    let expertiseDescription = "";

    if (technicalDetails.tools.length > 0 || 
        technicalDetails.technologies.length > 0 || 
        technicalDetails.methodologies.length > 0) {

        if (technicalDetails.tools.length > 0) {
            expertiseDescription += `Experienced with tools: ${technicalDetails.tools.join(', ')}. `;
        }

        if (technicalDetails.technologies.length > 0) {
            expertiseDescription += `Familiar with technologies: ${technicalDetails.technologies.join(', ')}. `;
        }

        if (technicalDetails.methodologies.length > 0) {
            expertiseDescription += `Follows methodologies: ${technicalDetails.methodologies.join(', ')}. `;
        }
    }

    if (domainKnowledge) {
        expertiseDescription += `Domain knowledge: ${domainKnowledge} `;
    }

    if (certificatesList.length > 0) {
        expertiseDescription += `Holds certifications in: ${certificatesList.join(', ')}.`;
    }

    // 7. Create conversation-friendly search text
    // This is optimized for natural language questions to the LLM
    const conversationText = `
    ${roleDescription}
    ${skillsDescription}
    ${industryDescription}
    ${orgDescription}
    ${managementDescription}
    ${expertiseDescription}
    ${note ? 'Additional info: ' + note : ''}
    `.trim();

    // 8. Create structured embedding text (for vector search)
    const structuredParts = [];

    // Start with name, technical domain and role category
    structuredParts.push(`Resource: ${resourceName} | ID: ${resourceId} | Technical Domain: ${technicalDomain} | Role: ${roleCategory} | Seniority: ${globalSeniority}`);

    // Add team info
    if (team || department || vertical) {
        structuredParts.push(`Organization: ${vertical || ''} | ${department || ''} | ${team || ''}`);
    }

    // Add management
    if (coeManager || engineeringLead || engineeringHead || director) {
        structuredParts.push(`Management: ${engineeringLead !== resourceName ? engineeringLead : ''} | ${coeManager} | ${engineeringHead} | ${director}`);
    }

    // Add primary skill areas
    if (primarySkillAreas.length > 0) {
        structuredParts.push(`Primary Skill Areas: ${primarySkillAreas.join(', ')}`);
    }

    // Add skills by level
    if (skills.senior.length > 0) {
        structuredParts.push(`Senior Skills: ${skills.senior.join(', ')}`);
    }

    if (skills.mid.length > 0) {
        structuredParts.push(`Mid Skills: ${skills.mid.join(', ')}`);
    }

    if (skills.junior.length > 0) {
        structuredParts.push(`Junior Skills: ${skills.junior.join(', ')}`);
    }

    // Add industries
    if (industries.length > 0) {
        structuredParts.push(`Industries: ${industries.join(', ')}`);
    }

    // Add extracted tools and technologies
    if (technicalDetails.tools.length > 0) {
        structuredParts.push(`Tools: ${technicalDetails.tools.join(', ')}`);
    }

    if (technicalDetails.technologies.length > 0) {
        structuredParts.push(`Technologies: ${technicalDetails.technologies.join(', ')}`);
    }

    if (technicalDetails.methodologies.length > 0) {
        structuredParts.push(`Methodologies: ${technicalDetails.methodologies.join(', ')}`);
    }

    // Add certificates
    if (certificatesList.length > 0) {
        structuredParts.push(`Certificates: ${certificatesList.join(', ')}`);
    }

    // Add miscellaneous notes if present
    if (note && note.trim()) {
        structuredParts.push(`Additional Info: ${note}`);
    }

    // Join with pipe separator
    const structuredText = structuredParts.filter(Boolean).join(' | ');

    // Full text for embedding combines structured and natural language
    const textToEmbed = `${structuredText} | ${conversationText}`;


  return {
    json: {
        id: resource.id,
        name: resource.name,
        skills_senior: skills_senior.join(", ") || "",
        skills_medium: skills_medium.join(", ") || "",
        skills_junior: skills_junior.join(", ") || "",
        industry: industries.join(", ") || "",
        description: description,
        section: section,
        seniority: seniority,
        team:team,
        department:department,
        vertical:vertical,
        coe_manager:coe_manager,
        el:el,
        eh:eh,
        director:director,
        employment_type:employment_type,
        certs: certs.join(", ") || "",
        note: note,
        embedding_facts_text: structuredText,
        embedding_convo_text: conversationText

        // embedding_text: embeddingText,
    }
  };
});
