// Define your project and task mappings here
const projectMappings = {
  // Example mappings - replace with your actual mappings
  'Project Client Support': {
    projectId: 262203,
    taskId: 14114655
  },
  'Project ConfToCode': {
    projectId: 471140,
    taskId: 15929830
  },

  // Add more mappings as needed
};

const start = new Date($json.start.dateTime || $json.start.date);
const end   = new Date($json.end.dateTime   || $json.end.date);

// NEW — force Zagreb tz
const minutes  = Math.round((end - start) / 60000);

const tz       = 'Europe/Zagreb';
const dateStr  = start.toLocaleDateString('en-CA', { timeZone: tz });            // 2025-05-24
const timeStr  = start.toLocaleTimeString('en-GB', { timeZone: tz, hour12:false }); // 18:00:00

$input.item.json = {
  ...$json,
  duration : minutes,
  date     : dateStr,
  time     : timeStr      // Teamwork node reads this
};



// Get meeting title
const meetingTitle = $input.item.json.summary;

// ─── keywords that should map to the "Client support" project ─────
const clientSupportTitles = [
  'Client Support sync',
  'RC - Support sync',
  'ESS - sync',
  'Kudos & Bonus za Support developere',
  'Preventive maintenance sync',
  'ESS - sync',
  // add more phrases here, lowercase please
];

// meeting title (already lower-cased earlier in your code)
const isClientSupport = clientSupportTitles.some(k =>
  meetingTitle.includes(k)
);

if (isClientSupport) {
  return {
    json: {
      ...$input.item.json,
      projectId: 262203,   // Client support project
      taskId   : 14114655, // Internal General task
      hasTask  : true
    }
  };
}

// ─── keywords that should map to the "ConfToCode" project ─────
const confToCodeTitles = [
  'ConfToCode weekly team meet',
  'ConfToCode - weekly',
  // add more phrases here, lowercase please
];

// meeting title (already lower-cased earlier in your code)
const isConfToCode = confToCodeTitles.some(k =>
  meetingTitle.includes(k)
);

if (isConfToCode) {
  return {
    json: {
      ...$input.item.json,
      projectId: 471140,   // Client support project
      taskId   : 15929830, // Internal General task
      hasTask  : true
    }
  };
}

// ─── keywords that should map to the "HARVN" project ─────
const harvnTitles = [
  'harvn',  // covers HARVN, Harvn, harvn
];

const isHarvn = harvnTitles.some(k =>
  meetingTitle.toLowerCase().includes(k)
);

if (isHarvn) {
  return {
    json: {
      ...$input.item.json,
      projectId: 715062,   // HARVN Post MSP project
      taskId   : 17002801, // HARVN task
      hasTask  : true
    }
  };
}



// Find matching project for other meetings
let selectedProject = null;
for (const [key, value] of Object.entries(projectMappings)) {
  if (meetingTitle.includes(key)) {
    selectedProject = value;
    break;
  }
}

// If no match found, use default project
if (!selectedProject) {
  selectedProject = {
    projectId: 317287,
    hasTask: false
  };
}

// Add project and task IDs to the output
return {
  json: {
    ...$input.item.json,
    projectId: selectedProject.projectId,
    taskId: selectedProject.taskId
  }
};
